mapper.select:
select 

from sys_resource
where  1=1 

    and available = #{available}


    and name like '%${name}%'


    and type = #{type}


    and parent_id = #{parentId}

select

from sys_resource
where  1=1

  and available = #{resource.available}


  and name like '%${resource.name}%'


  and type = #{resource.type}


  and parent_id = #{resource.parentId}
